import React, { useState, useContext, useEffect } from 'react';
import ContactContext from '../../context/contact/contactContext';

export const ContactForm = () => {
  const contactContext = useContext(ContactContext);
  const [contact, setContact] = useState({
    name: '',
    phone: '',
    email: '',
    type: 'personal'
  });
  const { name, phone, email, type } = contact;
  const { addContact, current } = contactContext;

  useEffect(() => {
    if (current !== null) {
      setContact(current);
    } else {
      setContact({
        name: '',
        phone: '',
        email: '',
        type: 'personal'
      });
    }
  }, [contactContext, current]);

  const onSubmit = e => {
    e.preventDefault();
    addContact(contact);
    setContact({
      name: '',
      phone: '',
      email: '',
      type: 'personal'
    });
  };

  const onChange = e =>
    setContact({
      ...contact,
      [e.target.name]: e.target.value
    });

  return (
    <form onSubmit={onSubmit}>
      <h2 className='text-primary'>
        {current ? 'Edit Contact' : 'Add contact'}
      </h2>
      <input
        type='text'
        placeholder='Name'
        name='name'
        value={name}
        onChange={onChange}
      />
      <input
        type='email'
        placeholder='Email'
        name='email'
        value={email}
        onChange={onChange}
      />
      <input
        type='text'
        placeholder='Phone'
        name='phone'
        value={phone}
        onChange={onChange}
      />
      <h5>Contact type</h5>
      <div>
        <input
          type='radio'
          name='type'
          value='personal'
          checked={type === 'personal'}
          onChange={onChange}
        />
        {'  '}
        Personal
        <input
          type='radio'
          name='type'
          value='professional'
          onChange={onChange}
          checked={type === 'professional'}
        />
        {'  '}
        Professional
      </div>

      <div>
        <input
          type='submit'
          value={current ? 'Update Contact' : 'Add contact'}
          className='btn btn-primary btn-block'
        />
      </div>
    </form>
  );
};

export default ContactForm;
