import React, { useState } from 'react';
// import ContactState from '../../context/contact/ContactState';

export const ContactForm = () => {
  const [contact, setContact] = useState({
    name: '',
    phone: '',
    email: '',
    type: 'personal'
  });

  const onChange = e =>
    setContact({
      ...contact,
      [e.target.name]: e.target.value
    });
  const { name, phone, email, type } = contact;

  return (
    <form>
      <h2 className='text-primary'>Add Contact</h2>
      <input type='text' placeholder='Name' value={name} onChange={onChange} />
      <input
        type='email'
        placeholder='Email'
        value={email}
        onChange={onChange}
      />
      <input
        type='text'
        placeholder='Phone'
        value={phone}
        onChange={onChange}
      />
      <h5>Contact type</h5>
      <input
        type='radio'
        name='type'
        value='personal'
        checked={type === 'personal'}
        onChange={onChange}
      />{' '}
      Personal
      <input
        type='radio'
        name='type'
        value='professional'
        onChange={onChange}
        checked={type === 'professional'}
      />{' '}
      Professional
      <div>
        <input
          type='submit'
          value='Add contact'
          className='btn btn-primary btn-block'
        />
      </div>
    </form>
  );
};

export default ContactForm;
